import logo from '../../../assets/MainLogo.png';
//import './App.css';

function App() {
    return (
        <Layout>
            <Header
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    background: 'white',    
                    position: 'fixed',
                    width: '100%',
                    zIndex: 9
                }}
            >
                <ToastContainer />
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        width: '100%',
                        alignItems: 'center',
                        background: 'white',
                    }}>

                    <div style={{
                        display: 'flex',
                    }}>
                        <img src={logo} height={35} alt='logo' />
                    </div>
                </div>
            </Header>
            <Layout
                style={{
                    marginTop: '50px',
                    background: 'white',
                }}>
                <div
                    style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        height: '800px',
                        flexDirection: 'column'
                    }}
                >
                    <Card style={{
                        padding: '12px',
                        margin: '12px',
                        minWidth: 340

                    }}>
                        <div style={{
                            margin: '8px auto 24px',
                            textAlign: 'center'
                        }}>
                            <h2>Log In</h2>
                        </div>
                        <Form
                            name="login-form"
                            autoComplete="off"
                            onFinish={onLoginClick}
                        >
                            <Form.Item
                                name="email"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Please input your email',
                                    },
                                ]}
                            >
                                <Input
                                    placeholder='Email'
                                    size='large'
                                />
                            </Form.Item>

                            <Form.Item
                                name="password"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Please input your password!',
                                    }
                                ]}
                            >
                                <Input.Password
                                    size='large'
                                    placeholder='Password' />
                            </Form.Item>
                            <div style={{
                                textAlign: 'end',
                                margin: '4px 0 12px'
                            }}>
                                <Link to={'/forgotPassword'}>
                                    Forgot Password?
                                </Link>
                            </div>
                            <Form.Item>
                                <Button
                                    htmlType="submit"
                                    size='large'
                                    type="primary"
                                    style={{
                                        width: '100%',
                                    }}
                                >
                                    Login
                                </Button>
                                <div style={{
                                    margin: '12px 0',
                                    textAlign: 'center'
                                }}>Don't have an account?
                                    <Link to={'/signup'}>
                                        {' '}Register
                                    </Link>
                                </div>
                            </Form.Item>
                        </Form>
                        {errorMessage && <div id="errorDiv" style={{ 
                                    margin: '12px 0',
                                    textAlign: 'center',
                                    color: 'red',
                                    fontWeight: 'bold',
                                    // display: 'none'
                                }}>
                        </div>}
                    </Card>

                </div>
            </Layout>
        </Layout >
    );
}

export default App;
